IF EXISTS(SELECT 1 FROM sys.tables WHERE object_id = OBJECT_ID('Telefono'))
BEGIN;
    DROP TABLE [Telefono];
END;
GO

CREATE TABLE [Telefono] (
    [TelefonoID] INTEGER NOT NULL IDENTITY(1, 1),
    [idTelefono] INTEGER NULL,
    [numeroTelefono] VARCHAR(13) NULL,
    [Persona_idPersona] INTEGER NULL,
    PRIMARY KEY ([TelefonoID])
);
GO


